      //logic for signup(register)
      fetch(

        'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCnX2BY1cIpsHi24sP5Nprg1sAnUS-IS9w',

        {

          method: "POST",

          body: JSON.stringify({

            email: enteredEmail,

            password: enteredPassword,

            returnSecureToken: true,

          }),
          headers: { "Content-Type": "application/json" },

        }

      ).then((res) => {

        //res is ok :show success message, redirecting to some other page

        if(res.ok){



        }


    }
      )
  }
}


  return (
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={submitHandler}>
        <div className={classes.control}>
          <label htmlFor='email'>Your Email</label>
          <input type='email' id='email' required  ref={emailRef} />
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Your Password</label>
          <input type='password' id='password' required  onChange={passwordRef}/>
        </div>
        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
  );
};

export default AuthForm;
